let "month=30*24*60*60" ; now=$(date +%s) ; let "blah=$now-$month" ;echo " curl -X POST http://127.0.0.1:52921/json_rpc -d '{\"jsonrpc\":\"2.0\",\"id\":\"0\",\"method\":\"get_transfers\",\"params\":{\"out\":true,\"account_index\":0}}' -H 'Content-Type: application/json' | jq '.result[] | .[] | select (.timestamp >= $blah ) | {txid: .txid, amount: .amount, date: .timestamp | strftime(\"%d-%m-%Y\")} ' " | bash > data.txt
